  val NMI  = UInt(1.W)
  val _0   = UInt(1.W)
  val Impl = UInt(2.W)
  
  val IM = Vec(8, Bool())
  
  val KX   = UInt(1.W)
  val SX   = UInt(1.W)
  val UX   = UInt(1.W)
  val UM   = UInt(1.W) // --\
                       //   +--> KSU
  val R0   = UInt(1.W) // --/
