  val EXL  = Bool()  // 0 for user, 1 for kernel
  val IE   = Bool()  // 0 for disable, 1 for enable

  def init(param: PlatformConfig): Unit = {
    require(this.getWidth == 32)
    this.CU := 1.U
    this.RP := 0.U
    this.FR := 0.U
    this.RE := 0.U
    this.MX := 0.U

    this.PX := 0.U
    this.BEV := 1.U

    this.TS := 0.U
    this.SR := 0.U
    this.NMI := 0.U
    this._0 := 0.U
    this.Impl := 0.U

    this.IM := Fill(this.IM.getWidth, false.B).asTypeOf(this.IM)

    this.KX := 0.U
    this.SX := 0.U
    this.UX := 0.U
    this.UM := 0.U

    this.R0 := 0.U
    this.ERL := 1.U
    this.EXL := 0.U
    this.IE := 0.U
  }

  def write(value: UInt): Unit = {
    val newVal = value.asTypeOf(this)
    this.CU := newVal.CU
    this.RP := newVal.RP
    this.RE := newVal.RE
    this.BEV := newVal.BEV
    this.TS := newVal.TS
    this.SR := newVal.SR
    this.NMI := newVal.NMI
    this.IM := newVal.IM
    this.UM := newVal.UM
    this.ERL := newVal.ERL
    this.EXL := newVal.EXL
    this.IE := newVal.IE
  }
}

